value:
{
  "comment" : [
    "Default filter for Hive services.",
    "Discards events generated by Hive MR jobs in /tmp directory"
  ],
  "defaultAction" : "accept",
  "rules" : [
    {
      "action" : "discard",
      "fields" : [
        { "name" : "operation", "match" : "QUERY" },
        { "name" : "objectType", "match" : "DFS_DIR"},
        { "name" : "resourcePath", "match" : "/tmp/hive-(?:.+)?/hive_(?:.+)?/-mr-.*" }
      ]
    }
  ]
}
